import React from 'react';
import { NavLink } from 'react-router-dom';

export default function NavigationBar(){
  const items = ['all','best-sellers','mobiles','electronics','fashion','home','appliances','toys-baby','grocery'];
  return (
    <div style={{background:'#0f1111', color:'#fff'}}>
      <div className="container nav-scroll" style={{display:'flex', gap:20, padding:'8px 0', overflowX:'auto'}}>
        {items.map((it,idx)=> (
          <NavLink key={idx} to={`/category/${it}`} style={({isActive})=>({color:isActive? '#ffd814' : '#fff', fontSize:14, textTransform:'capitalize'})}>{it.replace(/-/g,' ')}</NavLink>
        ))}
      </div>
    </div>
  );
}
